import { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { useDispatch } from "react-redux";
import { getUsers, getbydomain } from "../../actions/index";
import Student from "./card/card";
import { Button } from "bootstrap";
import Form from "react-bootstrap/Form";
import { Grid, CircularProgress } from "@material-ui/core";
import { Link } from "react-router-dom";
import { useQuery } from "react-query";
import axios from "axios";

function Card(props) {
  // let { isloading, users } = useSelector((state) => state.users);
  const fetchData=()=>{
    return axios.get('http://localhost:4000/Users')
   }
   
   const onSuccess=(data)=>{
    console.log('Perform side effect after data fetching');
  }
  
  const onError=(error)=>{
    console.log('Perform sideffect on encountring error');
  }

  const { isLoading, data,isFetching } = useQuery("users", fetchData);
  
 
  let users=data?.data
  console.log(users);
  
  if (props.stream !== "ALL") {
    users = users.filter((data) => {
      return props.stream === data.domain;
    });
    if (!users.length) return `no user found with domain ${props.stream}`;
  }
  
  // if (!users.length && !isLoading) return "no user found";
  return (
    <>
      {isLoading ? (
        <div style={{ marginLeft: "50%", marginTop: "10px" }}>
          <CircularProgress />
        </div>
      ) : (
           
        <Grid
          container
          alignItems="stretch"
          spacing={10}
          style={{ marginLeft: "2rem" }}
        >
          {users.map((user) => (
            <Grid key={user.id} item xs={12} sm={6} md={3}>
              <Student user={user} props={props.props} />
            </Grid>
          ))}
        </Grid>
      )}
    </>
  );
}

export default Card;
